    /**
     * @dataProvider getValidFixtures
     * @param string $hex
     * @param string $base64
     */
    public function testValidFixtures(string $hex, string $base64)
    {
        $raw = new Buffer(base64_decode($base64));
        if ($raw->getHex() !== $hex) {
            $this->fail("Inconsistent test fixtures");
        }
        $raw = Buffer::hex($hex);
        $psbt = PSBT::fromBuffer($raw);
        $this->assertEquals($raw->getBinary(), $psbt->getBuffer()->getBinary());
    }

    public function testGetValues()
    {
        $unsignedTx = new Transaction(0, [
            new TransactionInput(new OutPoint(Buffer::hex('', 32), 0xffffffff), new Script()),
            new TransactionInput(new OutPoint(Buffer::hex('', 32), 0xffffffff), new Script()),
            new TransactionInput(new OutPoint(Buffer::hex('', 32), 0xffffffff), new Script()),
        ], [
            new TransactionOutput(1, new Script()),
            new TransactionOutput(1, new Script()),
        ]);
        $unknowns = [];
        $inputs = [new PSBTInput(), new PSBTInput(), new PSBTInput()];
        $outputs = [new PSBTOutput(), new PSBTOutput()];
        $psbt = new PSBT($unsignedTx, $unknowns, $inputs, $outputs);
        $this->assertSame($unsignedTx, $psbt->getTransaction());
        $this->assertSame($unknowns, $psbt->getUnknowns());
        for ($i = 0; $i < count($unsignedTx->getInputs()); $i++) {
            $this->assertSame($inputs[$i], $psbt->getInputs()[$i]);
        }
        for ($i = 0; $i < count($unsignedTx->getOutputs()); $i++) {
            $this->assertSame($outputs[$i], $psbt->getOutputs()[$i]);
        }
    }

    public function testUpdate()
    {
        $unsignedTx = new Transaction(0, [
            new TransactionInput(new OutPoint(Buffer::hex('', 32), 0xffffffff), new Script()),
        ], []);
        $unknowns = [];
        $inputs = [new PSBTInput(),];
        $outputs = [];
        $psbt = new PSBT($unsignedTx, $unknowns, $inputs, $outputs);
        $this->assertFalse($psbt->getInputs()[0]->hasWitnessTxOut());
        $txOut = new TransactionOutput(1, new Script());
        $psbt->updateInput(0, function (UpdatableInput $input) use ($txOut): UpdatableInput {
            $input->addWitnessTxOut($txOut);
            return $input;
        });
        $this->assertTrue($psbt->getInputs()[0]->hasWitnessTxOut());
        $this->assertSame($txOut, $psbt->getInputs()[0]->getWitnessTxOut());
    }

    /**
     * @expectedException \BitWasp\Bitcoin\Exceptions\InvalidPSBTException
     * @expectedExceptionMessage Invalid number of inputs
     */
    public function testChecksNumInputsMatchesGreaterThan()
    {
        $unsignedTx = new Transaction(0, [
            new TransactionInput(new OutPoint(Buffer::hex('', 32), 0xffffffff), new Script()),
        ], []);

        new PSBT($unsignedTx, [], [new PSBTInput(), new PSBTInput()], []);
    }

